#pragma once 
#include <SequencerScripting_Structs.h>
 
 
 
class UMovieSceneBindingExtensions
{
public:
	UMovieSceneBindingExtensions(std::uint64_t p_addr = 0) : m_addr(p_addr) {}

	std::uint64_t GetAddress() { return m_addr; }

	bool IsValidClass() { return m_addr > 0; }


private:
	std::uint64_t m_addr = 0;
};


class UMovieSceneScriptingChannel
{
public:
	UMovieSceneScriptingChannel(std::uint64_t p_addr = 0) : m_addr(p_addr) {}

	std::uint64_t GetAddress() { return m_addr; }

	bool IsValidClass() { return m_addr > 0; }

	struct FName GetChannelName() {
		return memory.read<struct FName>(m_addr + 40);
	}

private:
	std::uint64_t m_addr = 0;
};


